import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom/cjs/react-router-dom";


const ClientList = () => {
  const [client, setClient] = useState([]);

  useEffect(() => {
    const fetchClients = async () => {
      const response = await fetch("http://localhost:8080/clients");
      const data = await response.json();
      console.log(data); 
      setClient(data);
    };
    fetchClients();
  }, []);
  const remove = async (id) => {
    await fetch(`http://localhost:8080/clients/${id}`, {
        method: 'DELETE',
    });
    setClient(client.filter(client => client.id !== id)); // Update state to remove the deleted client
};

  return (
    <div>
      <div>
        <h2>clients</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
  {client.map(client => (
    <tr key={client.id}>
      <td>{client.name}</td>
      <td>{client.email}</td>
      <td>
        
          <button size="sm" color="primary" tag={Link} to={`/clients/${client.id}`}>Edit</button>
          <button size="sm" color="danger" onClick={() => remove(client.id)}>Delete</button>
        
      </td>
    </tr>
  ))}
</tbody>


        </table>
        <button>addClient</button>
      </div>
    </div>
  );
};

export default ClientList;
