import { Button } from "bootstrap";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom/cjs/react-router-dom";
import { ButtonGroup } from "reactstrap";

const ClientList = () => {
  const [client, setClient] = useState([]);

  useEffect(() => {
    const fetchClients = async () => {
      const response = await fetch("http://localhost:8080/clients");
      const data = response.json();
      setClient(data);
    };
    fetchClients();
  }, []);
  const remove = async (id) => {
    await fetch(`/clients/${id}`, {
        method: 'DELETE',
    });
    setClient(client.filter(client => client.id !== id)); // Update state to remove the deleted client
};

  return (
    <div>
      <div>
        <h2>clients</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {client.map(client => (
                      <tr key={client.id}>
                           <td>{client.name}</td>
                           <td>{client.email}</td>
                           <td>


                                    <ButtonGroup>
                                        <Button size="sm" color="edit" tag={Link} to={`/clients/${client.id}`}>Edit</Button>
                                        <Button size="sm" color="danger" onClick={() => remove(client.id)}>Delete</Button>
                          </ButtonGroup>
                           </td>

                      </tr>
            ))};
          </tbody>
        </table>
        <button>addClient</button>
      </div>
    </div>
  );
};

export default ClientList;
