import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { Form, FormGroup, Input, Button, Container } from 'reactstrap';

const ClientEdit = () => {
    const emptyItems = {
        name: '',
        email: ''
    };
    const [items, setItems] = useState(emptyItems);
    const { id } = useParams();
    const history = useHistory();

    useEffect(() => {
        const fetchClients = async () => {
            if (id !== 'new') {
                try {
                    const response = await axios.get(`http://localhost:8080/clients/${id}`);
                    setItems(response.data);
                } catch (error) {
                    console.log('Error fetching client data:', error);
                }
            }
        };
        fetchClients();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setItems((prevItem) => ({
            ...prevItem,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const url = `http://localhost:8080/clients${items.id ? '/' + items.id : ''}`;
            const method = items.id ? 'put' : 'post';
            await axios({
                method: method,
                url: url,
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                data: items
            });
            history.push('/clients');
        } catch (error) {
            console.log('There was an error!', error);
        }
    };

    return (
        <div>
            <Container>
                <Form onSubmit={handleSubmit}>
                    <FormGroup>
                        <label htmlFor="name">Name</label>
                        <Input type="text" name="name" id="name" value={items.name || ''} onChange={handleChange} />
                    </FormGroup>
                    <FormGroup>
                        <label htmlFor="email">Email</label>
                        <Input type="text" name="email" id="email" value={items.email || ''} onChange={handleChange} />
                    </FormGroup>
                    <FormGroup>
                        <Button>Save</Button>
                    </FormGroup>
                </Form>
            </Container>
        </div>
    );
};

export default ClientEdit;
