import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Button, Table, Container } from "reactstrap";

const ClientList = () => {
  const [client, setClient] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClients = async () => {
      const response = await fetch("http://localhost:8080/clients");
      const data = await response.json();
      console.log(data);
      setClient(data);
    };
    fetchClients();
  }, []);

  const remove = async (id) => {
    await fetch(`http://localhost:8080/clients/${id}`, {
      method: 'DELETE',
    });
    setClient(client.filter(client => client.id !== id)); // Update state to remove the deleted client
  };

  return (
    <Container>
      <h2 className="my-4">Clients</h2>
      <Table striped>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {client.map(client => (
            <tr key={client.id}>
              <td>{client.name}</td>
              <td>{client.email}</td>
              <td>
                <Button color="primary" onClick={() => navigate(`/clients/${client.id}`)}>Edit</Button>
                <Button color="danger" onClick={() => remove(client.id)} className="ml-2">Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Button color="success" onClick={() => navigate(`/clients/new`)}>Add Client</Button>
    </Container>
  );
};

export default ClientList;
